function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }

   if(newValue == g_form.getValue('mynum1')){
	alert("HIT!!  Target Number「"+ newValue +"」 = 'MyNumber1'");
	jslog("HIT!!  Target Number「"+ newValue +"」 = 'MyNumber1'");
   }else if(newValue == g_form.getValue('mynum2')){
	alert("HIT!!  Target Number「"+ newValue +"」 = 'MyNumber2'");
	jslog("HIT!!  Target Number「"+ newValue +"」 = 'MyNumber2'");
   }else if(newValue == g_form.getValue('mynum3')){
    alert("HIT!!  Target Number「"+ newValue +"」 = 'MyNumber3'");
	jslog("HIT!!  Target Number「"+ newValue +"」 = 'MyNumber3'");
   }else{
	alert("NOT HIT... Target Number「"+ newValue +"」  is nothing");
	jslog("NOT HIT... Target Number「"+ newValue +"」  is nothing");
   }

}
